def demo_CRF_lexical_analyzer(tests):
    """ CRF词法分析器
    """
    print("CRF词法分析器")
    start=time.time()
    CRFLexicalAnalyzer = JClass("com.hankcs.hanlp.model.crf.CRFLexicalAnalyzer")
    analyzer = CRFLexicalAnalyzer()
    ans=[]
    for sentence in tests:
        segs=analyzer.analyze(sentence)
        arr = str(segs).split(" ")
        ans.append(arr)
    ner = ['nr', 'nrf', 'nrj']
    name_list = {}
    for a in ans[0]:
        x = a.split('/')
        if x[1] in ner:
            if x[0] not in name_list:
                name_list[x[0]] = 1
            else:
                name_list[x[0]] += 1
    end = time.time()
    print("词性标注结果: ", ans)
    print("姓名列表: ", name_list)
    print("运行时间差: ", end - start)
    return ans, name_list

