    def demo_NLP_segment(self,tests):
        """ NLP分词，更精准的中文分词、词性标注与命名实体识别
            标注集请查阅 https://github.com/hankcs/HanLP/blob/master/data/dictionary/other/TagPKU98.csv
            或者干脆调用 Sentence#translateLabels() 转为中文
          """
        print("NLP分词")
        start = time.time()
        NLPTokenizer = JClass("com.hankcs.hanlp.tokenizer.NLPTokenizer")
        ans = []
        ner = ['nr', 'nrf', 'nrj']
        name_list = {}
        for sentence in tests:
            segs = NLPTokenizer.analyze(sentence)
            # for term in segs:
            #     print('{}\t{}'.format(term.value, term.label))  # 获取单词与词性
            segs=str(segs)
            arr = segs.split(" ")
            ans.append(arr)
            for a in arr:
                x = a.split('/')
                if x[1] in ner:
                    if x[0] not in name_list:
                        name_list[x[0]] = 1
                    else:
                        name_list[x[0]] += 1
        end = time.time()
        print("词性标注结果: ", ans)
        print("姓名列表: ", name_list)
        print("运行时间差: ", end - start)
        return ans, name_list

    def cut_sent(self,para):
        para = re.sub('([qrtu．。！？\?])([^”’])', r"\1\n\2", para)  # 单字符断句符
        para = re.sub('(\.{6})([^”’])', r"\1\n\2", para)  # 英文省略号
        para = re.sub('(\…{2})([^”’])', r"\1\n\2", para)  # 中文省略号
        para = re.sub('([。！？\?][”’])([^，。！？\?])', r'\1\n\2', para)
        # 如果双引号前有终止符，那么双引号才是句子的终点，
        # 把分句符\n放到双引号后，注意前面的几句都小心保留了双引号
        para = para.rstrip()  # 段尾如果有多余的\n就去掉它
        # 很多规则中会考虑分号;，但是这里我把它忽略不计，破折号、英文双引号等同样忽略，
        # 需要的再做些简单调整即可。
        return para.split("\n")
    def demo_chinese_name_recognition(self,sentences):
        """ 人名识别
        """
        # Segment = JClass("com.hankcs.hanlp.seg.Segment")
        # Term = JClass("com.hankcs.hanlp.seg.common.Term")
        print("人名识别")
        start = time.time()
        segment = HanLP.newSegment().enableNameRecognize(True)\
                                    .enableJapaneseNameRecognize(True) \
                                    .enableTranslatedNameRecognize(True)

        ans=[]
        ner = ['nr', 'nrf', 'nrj']
        name_list = {}
        for sentence in sentences:
            segs = segment.seg(sentence)
            arr = str(segs).split(" ")
            ans.append(segs)
            for a in segs:
                if str(a.nature) in ner:
                    if a.word not in name_list:
                        name_list[a.word] = 1
                    else:
                        name_list[a.word] += 1

        end = time.time()
        print("人名识别结果: ", ans)
        print("姓名列表: ", name_list)
        print("运行时间差: ", end - start)
        return ans, name_list
